;
 HELLO_2_bErrCheck:
_HELLO_2_bErrCheck:
   RAM_PROLOGUE RAM_USE_CLASS_4
   RAM_SETPAGE_CUR >HELLO_2_fStatus
   mov A,  [HELLO_2_fStatus]
   and A, HELLO_2_RX_BUF_ERROR                   ; Mask off Error status
   and [HELLO_2_fStatus], ~HELLO_2_RX_BUF_ERROR
